function getZhiHuIcon(link: string) {
  return `[![](https://img.shields.io/badge/-知乎-fff?style=flat-square&logo=Zhihu&labelColor=ffffff&color=fff)](${link})`
}

function getJueJinIcon(link: string) {
  return `[![](https://img.shields.io/badge/掘金-fff?style=flat-square&logo=data:image/jpg;base64,)](${link})`
}

function getTwitterIcon(name: string, link: string) {
  return `[![](https://img.shields.io/badge/-@${name}-1ca0f1?style=flat-square&labelColor=1ca0f1&logo=twitter&logoColor=white)](${link})`
}
